import java.io.IOException;import javax.servlet.RequestDispatcher;import javax.servlet.ServletException;import javax.servlet.http.HttpServlet;import javax.servlet.http.HttpServletRequest;import javax.servlet.http.HttpServletResponse;public class FrontServlet extends HttpServlet{		protected void doGet(HttpServletRequest req, HttpServletResponse res)		throws ServletException, IOException{					String path = req.getServletPath().replaceAll("/","");						String url = "/WEB-INF/jsp/test" + path + ".jsp";						RequestDispatcher rd = req.getRequestDispatcher(url);						rd.forward(req,res);	}}